import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import ReactCursorPosition, { INTERACTIONS } from 'react-cursor-position';
import App from './App';


class Example extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="example"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel />
      </ReactCursorPosition>
    );
  }
}

class Example2 extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="example2"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel />
      </ReactCursorPosition>
    );
  }
}

const PositionLabel = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  return (
    <div className="example__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`width:: ${width}`}<br />
      {`height: ${height}`}<br />
      {`isActive: ${isActive}`}<br />
      {`isPositionOutside: ${isPositionOutside ? 'true' : 'false'}`}<br />
      {`isMouseDetected: ${isMouseDetected ? 'true' : 'false'}`}<br />
            {`isTouchDetected: ${isTouchDetected ? 'true' : 'false'}`}
    </div>
  );
};

ReactDOM.render(
  <Example />,
  <Example2 />,
  document.getElementById('app')
);
