import React from 'react';
import './App.css';
import ReactCursorPosition, { INTERACTIONS } from 'react-cursor-position';
import pad_image_1 from "./color_2d_plot1.png";
import image_architecture from "./figure_with_LSR.png";
import ReactAudioPlayer from 'react-audio-player';
import pianorolls from './pianoroll_files';


var metric1 = 300;
var metric2 = 300;
var metric3 = 300;
var metric4 = 300;
var metric1_quantized = 1; // Math.floor(metric1 / 30); 
var metric2_quantized = 1; // Math.floor(metric2 / 30); 
var metric3_quantized = 5; // Math.floor(metric3 / 30); 
var metric4_quantized = 5; // Math.floor(metric4 / 30); 
var mp3_file_name = "midi_1_1_1_7.mp3";
var pianoroll_file_name = "midi_1_1_5_5.png"; // + metric1_quantized + "_" + metric2_quantized + "_" + metric3_quantized + "_" + metric4_quantized +".png";


function importAll(r) {
  let images = {};
  r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
  return images;
}

const images = importAll(require.context('./pianoroll_files', false, '/\.png/'));

{/* <img src={images['midi_1_1_5_5.png']} /> */}

<img src={pianorolls.midi_1_1_5_5.png} />


// function importAll(r) {
//   let images = {};
//   r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
//   return images;
// }

// const images = importAll(require.context('../public/pianoroll_files', false, /\.(png|jpe?g|svg)$/));


function DisplayMetrics(props){
  return (
      <h1 className = 'display_metrics'> Metrics {props.m1} {props.m2} {props.m3} {props.m4} </h1>
  );

};

function TextInput(props){
  return (
      <h1 className = 'text_input'> Input MIDI </h1>
  );

};

function TextOutputVariations(props){
  return (
      <h1 className = 'text_output_variations'> Output MIDI (Input Variations) </h1>
  );

};

class Pad1 extends React.Component {

  render() {
    return (
      <ReactCursorPosition className="pad1"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel1 />
        <ImageComponent />
      </ReactCursorPosition>

    );
  }
}

const PositionLabel1 = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  metric1 = x;
  metric2 = y;

  return (
    <div className="pad1__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`isActive: ${isActive}`}
      <DisplayMetrics m1 = {metric1} m2 = {metric2} m3 = {metric3} m4 = {metric4}/>
    </div>


  );
};


const ImageComponent = (props) => {
  return (
    <div className="imageDiv">
      <img src={pad_image_1} width="280" height="280" />
    </div>
  );

};

class Pad2 extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="pad2"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel2 />
      </ReactCursorPosition>
    );
  }
}

const PositionLabel2 = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  metric3 = x;
  metric4 = y;

  return (
    <div className="pad2__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`width: ${width}`}<br />
      {`height: ${height}`}<br />
      {`isActive: ${isActive}`}<br />
      {`isPositionOutside: ${isPositionOutside ? 'true' : 'false'}`}<br />
      {`isMouseDetected: ${isMouseDetected ? 'true' : 'false'}`}<br />
            {`isTouchDetected: ${isTouchDetected ? 'true' : 'false'}`}
