import React from 'react';
import './App.css';
import ReactCursorPosition, { INTERACTIONS } from 'react-cursor-position';
import pad_image_1 from "./color_2d_plot1.png";
import ReactAudioPlayer from 'react-audio-player';


var metric1 = 300;
var metric2 = 300;
var metric3 = 300;
var metric4 = 300;
var file_index = 0;

class Pad1 extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="pad1"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel1 />
        <ImageComponent />
      </ReactCursorPosition>
    );
  }
}

const PositionLabel1 = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  return (
    <div className="pad1__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`isActive: ${isActive}`}
    </div>
  );
};

const ImageComponent = (props) => {
  return (
    <div className="imageDiv">
      <img src={pad_image_1} width="280" height="280" />
    </div>
  );

};

class Pad2 extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="pad2"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel2 />
      </ReactCursorPosition>
    );
  }
}

const PositionLabel2 = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  return (
    <div className="pad2__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`width: ${width}`}<br />
      {`height: ${height}`}<br />
      {`isActive: ${isActive}`}<br />
      {`isPositionOutside: ${isPositionOutside ? 'true' : 'false'}`}<br />
      {`isMouseDetected: ${isMouseDetected ? 'true' : 'false'}`}<br />
            {`isTouchDetected: ${isTouchDetected ? 'true' : 'false'}`}
    </div>
  );
};

function Audio_Player_Source(props){
  var mp3_file_name = props.name;

  return (
    <ReactAudioPlayer
        className = "audio_player_source"
        src = {mp3_file_name}
        controls
    />
  );
}

function Audio_Player_Generated(props){
  var mp3_file_name = "./mp3_files/midi_1.mp3";
  var file_index_1 = 1;
  var file_index_2 = 2;

  if (props.m1 > 200 && props.m2 > 200 && props.m3 > 200 && props.m4 > 200){
    mp3_file_name = "./mp3_files/midi_" + file_index_1 + ".mp3";

  }
  else if (props.m1 < 200 && props.m2 < 200 && props.m3 < 200 && props.m4 < 200){
    mp3_file_name = "./mp3_files/midi_" + file_index_2 + ".mp3";

  }

  return (
    <ReactAudioPlayer
        className = "audio_player_generated"
        src = {mp3_file_name}
        controls
    />
  );
}

class X1_label extends React.Component {
  render() {
    return (
      <h1 className = 'x1_label' > Rhythmic Complexity Level <br/> Latent Dimension = 0</h1>
    );
  }
}

class Y1_label extends React.Component {
  render() {
    return (
      <h1 className = 'y1_label' >Note Range Level <br/> Latent Dimension = 1</h1>
    );
  }
}

class X2_label extends React.Component {
  render() {
    return (
      <h1 className = 'x2_label'>Note Density Level <br/> Latent Dimension = 2</h1>
    );
  }
}

class Y2_label extends React.Component {
  render() {
    return (
      <h1 className = 'y2_label'>Average Pitch Interval Level <br/> Latent Dimension = 3</h1>
    );
  }
}

class Pad1_X0 extends React.Component {
  render() {
    return (
      <h1 className = 'pad1_x0'>0</h1>
    );
  }
}

class Pad1_X1 extends React.Component {
  render() {
    return (
      <h1 className = 'pad1_x1'>10</h1>
    );
  }
}

class Pad1_Y1 extends React.Component {
  render() {
    return (
      <h1 className = 'pad1_y1'>10</h1>
    );
  }
}

class Pad2_X0 extends React.Component {
  render() {
    return (
      <h1 className = 'pad2_x0'>0</h1>
    );
  }
}

class Pad2_X1 extends React.Component {
  render() {
    return (
      <h1 className = 'pad2_x1'>10</h1>
    );
  }
}

class Pad2_Y1 extends React.Component {
  render() {
    return (
      <h1 className = 'pad2_y1'>10</h1>
    );
  }
}


function App() {
  return (

    <div className="App">
      <Audio_Player_Source name = "./mp3_files/midi_1.mp3" />
      <Pad1 />
      <Pad2 />
      <Audio_Player_Generated m1 = {metric1} m2 = {metric2} m3 = {metric3} m4 = {metric4} />
      <X1_label />
      <Y1_label />
      <X2_label />
      <Y2_label />
      <Pad1_X0/>
      <Pad1_X1/>
      <Pad1_Y1/>
      <Pad2_X0/>
      <Pad2_X1/>
      <Pad2_Y1/>

    </div>
  );
}

export default App;
