class Pad1 extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="pad1"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel1 />
        <ImageComponent />
      </ReactCursorPosition>
    );
  }
}



const PositionLabel1 = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  return (
    <div className="pad1__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`isActive: ${isActive}`}
    </div>
  );
};

const ImageComponent = (props) => {
  return (
    <div className="imageDiv">
      <img src={pad_image_1} width="380" height="380" />
    </div>
  );

};

class Pad2 extends React.Component {
  render() {
    return (
      <ReactCursorPosition className="pad2"
       activationInteractionMouse={INTERACTIONS.CLICK}
      >
        <PositionLabel2 />
      </ReactCursorPosition>
    );
  }
}

const PositionLabel2 = (props) => {
  const {
    detectedEnvironment: {
      isMouseDetected = false,
      isTouchDetected = false
    } = {},
    elementDimensions: {
      width = 0,
      height = 0
    } = {},
    isActive = false,
    isPositionOutside = false,
    position: {
      x = 0,
      y = 0
    } = {}
  } = props;

  return (
    <div className="pad2__label">
      {`x: ${x}`}<br />
      {`y: ${y}`}<br />
      {`width: ${width}`}<br />
      {`height: ${height}`}<br />
      {`isActive: ${isActive}`}<br />
      {`isPositionOutside: ${isPositionOutside ? 'true' : 'false'}`}<br />
      {`isMouseDetected: ${isMouseDetected ? 'true' : 'false'}`}<br />
            {`isTouchDetected: ${isTouchDetected ? 'true' : 'false'}`}
    </div>
  );
};


function App() {

  function sayHello() {
    // const midiPath = './midi_1.mid';
    // MidiPlayer.init(midiPath)
    // MidiPlayer.play();
    MidiPlayback.setPlaybackFile('./midi_1.midi')
    MidiPlayback.play()
  }

  return (

    <div className="App">
      <button onClick={sayHello}>
      Click me!
      </button>
      <Pad1 />
      <Pad2 />
      <h1>Hello, world!</h1>
      {/* <PlayMIDI /> */}
    </div>
  );
}

export default App;
