declare module 'react-native-midi-player' {
    export function initPlayer(uri: string): void;
    export function play(): void;
    export function pause(): void;
    export function stop(): void;
    export function setTempo(tempo: number): void;
    export function setReverb(preset: string): void;
    export function setSoundFont(font: string): void;

    export interface MidiPlayer {
        initPlayer(uri: string): void;
        play(): void;
        pause(): void;
        stop(): void;
        setTempo(tempo: number): void;
        setReverb(preset: string): void;
        setSoundFont(font: string): void;
    }

    const MidiPlayer: MidiPlayer;

    export default MidiPlayer;
}
